# Bastien Wiaux et Alexandre Winand
import sorted_belgian_communes as sbc

def test(bool):
    if bool:
        print("ca marche")
    else:
        print("il y a un problème ...")

def test_verify_order():
    test(sbc.verify_order(sbc.all_communes) == True)
    test(sbc.verify_order( [("Aarschot", (629867.1340910662, 5649141.00455739)),("Boom", (596785.2232017588, 5665558.287847248)),\
    ("Affligem", (578131.916279454, 5639292.55774853)),("Aiseau-Presles", (611605.8849598696, 5585675.111576218)),\
    ("Bassenge", (661843.2732558982, 5637521.925917723)),("Alveringem", (476862.9305763073, 5647819.327231347)),\
    ("Amay", (664097.4560020705, 5603092.769621753)),("Amel", (299755.9311196781, 5581179.214980542)),\
    ("Andenne", (644734.2696348976, 5593989.501413442)),("Anderlues", (590023.892719608, 5583872.888351512)),\
    ("Anhe", (629493.8010256662, 5575681.781995778)),("Ans", (676932.3917471502, 5616676.558691478)),\
    ("Anthisnes", (677323.0513960029, 5596288.086637169))]) == False)

def test_coordinate():
    test(sbc.coordinate("Bassenge",sbc.all_communes) == (683747.6304687974, 5626807.335503256))
    test(sbc.coordinate("Zemst",sbc.all_communes) == (604132.2884060409, 5648281.131042825))
    test(sbc.coordinate("Nandrin",sbc.all_communes) == (669828.6076405094, 5599279.558297606))

def test_distance():
    test(sbc.distance("Bassenge","Affligem",sbc.all_communes) == 106351.11592380665)
    test(sbc.distance("Zemst","Anhe",sbc.all_communes) == 76901.70222049959)
    test(sbc.distance("Nandrin","Anderlues",sbc.all_communes) == 81278.27509509666)

